INSERT INTO `bz_comments` VALUES (1,1,'Một người bình luận WordPress','wapuu@wordpress.example','https://wordpress.org/','','2018-04-21 01:44:00','2018-04-21 01:44:00','Xin chào, đây là một bình luận\nĐể bắt đầu với quản trị bình luận, chỉnh sửa hoặc xóa bình luận, vui lòng truy cập vào khu vực Bình luận trong trang quản trị.\nAvatar của người bình luận sử dụng <a href=\"https://gravatar.com\">Gravatar</a>.',0,'1','','',0,0);
